def retransform(opened_cells, opened_rewards, W = 15, L = 15):
    observed_bandit = [[] for i in range(W*L)]
    hidden = np.array([True]*L*W)
    
    for cell in opened_cells:
        reward = opened_rewards[cell][0]
        if reward != "?":
            hidden[cell] = False 
            observed_bandit[cell].append(reward)
        else:
            highlighted = cell
    
    return(list(hidden), observed_bandit, highlighted)

hiddenlist = []
ob_b_list = []
highlighted_list = []
for i in range(20):
    (hidden, observed_bandit, highlighted) = retransform(eval("open" + str(i)), eval("rewards" + str(i)))
    hiddenlist.append(hidden)
    ob_b_list.append(observed_bandit)
    highlighted_list.append(highlighted)